import React from 'react';
import "./Card.css";
import { categories } from "../../helper/Data";
import reactsvg from "../assets/react.svg";

function Card() {
    function changeDiv() {
        const cardDiv = document.querySelectorAll(".card")[0];
        const cardOpt= document.querySelectorAll(".card-over")[0]
        cardDiv.style.display="none"
        cardOpt.style.display="visible"
    }
    return (
        <div>
            <div className="card-header">
                <img src={reactsvg} alt="react"></img>
            </div>
            <div className="card-container">
                <h1 className="cards-title">Languages</h1>
                <div className="cards">
                    {categories.map((categories) => {
                        return (
                            <div className="card" onClick={changeDiv}>
                                <img src={categories.img} alt={categories.name}></img>
                                <div className="name">
                                    <h2>{categories.name}</h2>
                                </div>
                                <div className="card-over">
                                    <p>{categories.options}</p>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
    )
}

export default Card;
